mapper.insert:
		INSERT INTO EVENT(
			 EVENT_ID,EVENT_NAME, DESCRIPTION, ARTIST_ID, IS_PAID, START_DATE, END_DATE,
			VENUE_ID, GALLERY_PATH, CATEGORY_ID)
		VALUES( #{eventId},#{eventName},#{description},#{artistId},#{isPaid},#{startDate},#{endDate}
				,#{venueId},#{galleryPath},#{categoryId});

        SELECT LAST_INSERT_ID();

mapper.update:
UPDATE EVENT 
SET GALLERY_PATH = #{galleryPath}
WHERE EVENT_ID = #{eventId}

mapper.select:
SELECT * FROM Event  



	      event_name like #{eventName} AND start_date > #{startDate} AND end_date < #{endDate} 





			      event_name like #{eventName} 



			      start_date >= #{startDate}  AND (start_date < #{startDate}  AND end_date >= #{startDate} )



			      end_date >= curDate() AND end_date <= #{endDate} 



			      start_date >= #{startDate} AND end_date <= #{endDate} 







	  		order by start_date


	  		order by end_date


	  		order by event_name
